package btree.java;

public class Btree {
    
    public static class Node {
        Object nodeValue;

        public Node(Integer value) {
            this.nodeValue = value;
        }

        public Node(String value) {
            this.nodeValue = value;
        }

        public Node(Float value) {
            this.nodeValue = value;
        }

        @Override
        public String toString() {
            String nodeString = "Node()";
            
            if (this.nodeValue instanceof Integer) {
                nodeString = String.format("Node(%d)", this.nodeValue);
            } else if (this.nodeValue instanceof String) {
                nodeString = String.format("Node(%s)", this.nodeValue);
            } else if (this.nodeValue instanceof Float) {
                nodeString = String.format("Node(%.2f)", this.nodeValue);
            }

            return nodeString;
        }
    }

    public Btree() {}

    public static void main(String... args) {
        final String message = "\n"
            + "Btree-java is a port of the (Python) Binarytree library.\n"
            + "It has most of Binarytree's functionality, but lacks support for Graphviz and Jupyter Notebooks.\n"
            + "Btree-java includes matching unit tests.\n";
        
        System.out.println(message);
    }
}


